        public static long Execute(string input)
        {
            return CalculateFloor(input);
        }

        private static long CalculateFloor(string input)
        {
            var currentFloor = 0L;
            var index = 0;
            foreach (var c in input)
            {
                index++;

                if (c == '(')
                {
                    currentFloor++;
                } else if (c == ')')
                {
                    currentFloor--;
                }

                if (currentFloor == -1)
                {
                    return index;
                }
            }

            return index;
        }
